mapper.delete:
delete from userInfo
where 1=1 and id in

	#{user.id}

mapper.select:
select * from userInfo where 1=1
and name like concat(concat('%',#{user.name}),'%')
and nickname like concat(concat('%',#{user.nickName}),'%')
and province = #{user.province}
and city = #{user.city}
and area = #{user.area}
